import React from 'react';
import './style.css';



function BaseStatsElement(props) {

    function setColor(value) {
        var color = '';

        if(value <= 30) {
            color = '#f34444';
        } else if(value <= 60) {
            color = '#ff7f0f';
        } else if(value <= 90) {
            color = '#ffdd57';
        } else if(value <= 120) {
            color = '#a0e515';
        } else if(value <= 150) {
            color = '#23cd5e';
        } else {
            color = '#00c2b8';
        }

        return color;
    }



    return (
        <div className='baseStats'>
            <h2>Stats Base</h2>
            <table className='baseStatsTable'>
                <tbody>
                    <tr>
                        <th>HP</th>
                        <td className='cellNum'>{props.baseStats.hp}</td>
                        <td className='cellBarchart'>
                            <div className='barchartBar'  style={{width:`${(props.baseStats.hp * 100) / 180}%`, backgroundColor:{backgroundColor:`${setColor(props.baseStats.hp)}`}}} ></div>
                        </td>
                    </tr>
                </tbody>
            </table>

        </div>




    );
}

export default BaseStatsElement;
