import React, {useEffect, useState} from 'react';
import {Form, Button} from 'react-bootstrap';
import api from './../../service/index';


function DataElement() {

    const [pokemon, setPokemon] = useState({});
    const [name, setName] = useState('');
    const [hpIv, setHpIv] = useState(0);
    const [attackIv, setAttackIv] = useState(0);
    const [defenseIv, setDefenseIv] = useState(0);
    const [spAttackIv, setSpAttackIv] = useState(0);
    const [spDefenseIv, setSpDefenseIv] = useState(0);
    const [speedIv, setSpeedIv] = useState(0);
    const [attackBase, setAttackBase] = useState(0);
    const [defenseBase, setDefenseBase] = useState(0);
    const [spAttackBase, setSpAttackBase] = useState(0);
    const [spDefenseBase, setSpDefenseBase] = useState(0);
    const [speedBase, setSpeedBase] = useState(0);


    async function handlerSubmit(e) {
        e.preventDefault();
        const response = await api.get(`/${name}`);
        setPokemon(response.data);
    }

    useEffect(() => {
        console.log(pokemon.stats);
    }, [pokemon]);







    return(
        <div>
            <label>Nature:</label>
            <select name="select">
                    <option value="adamant">Adamant</option>
                    <option value="bashful">Bashful</option>
                    <option value="brave">Brave</option>
                    <option value="bold">Bold</option>
                    <option value="calm">Calm</option>
                    <option value="careful">Careful</option>
                    <option value="docile">Docile</option>
                    <option value="gentle">Gentle</option>
                    <option value="hardy">Hardy</option>
                    <option value="hasty">Hasty</option>
                    <option value="impish">Impish</option>
                    <option value="jolly">Jolly</option>
                    <option value="lax">Lax</option>
                    <option value="lonely">Lonely</option>
                    <option value="mild">Mild</option>
                    <option value="modest">Modest</option>
                    <option value="naive">Naive</option>
                    <option value="naughty">Naughty</option>
                    <option value="quiet">Quiet</option>
                    <option value="quirky">Quirky</option>
                    <option value="rash">Rash</option>
                    <option value="relaxed">Relaxed</option>
                    <option value="sassy">Sassy</option>
                    <option value="serious">Serious</option>
                    <option value="timid">Timid</option>

            </select>

            <hr/>
            <Form onSubmit={handlerSubmit}>
                <Form.Group controlId="formName">
                    <Form.Label>Nome do Pokemon</Form.Label>
                    <br/>
                    <Form.Control required value={name} onChange={(e)=> setName(e.target.value)}  type="text" />
                </Form.Group>
                <Form.Group controlId="formIVs">
                    <Form.Label>Informe as IVs do Pokemon</Form.Label>
                    <br/>
                    <Form.Label>HP</Form.Label>
                    <Form.Control required value={hpIv} onChange={(e)=> setHpIv(e.target.value)} type='text'/>
                    <br/>
                    <Form.Label>Attack</Form.Label> 
                    <Form.Control required value={attackIv} onChange={(e)=> setAttackIv(e.target.value)} type='text'/>
                    <br/>
                    <Form.Label>Defense</Form.Label> 
                    <Form.Control required value={defenseIv} onChange={(e)=> setDefenseIv(e.target.value)} type='text'/>
                    <br/>
                    <Form.Label>Sp Attack</Form.Label> 
                    <Form.Control required value={spAttackIv} onChange={(e)=> setSpAttackIv(e.target.value)} type='text'/>
                    <br/>
                    <Form.Label>Sp Defense</Form.Label> 
                    <Form.Control required value={spDefenseIv} onChange={(e)=> setSpDefenseIv(e.ttarget.value)} type='text'/>
                    <br/>
                    <Form.Label>Speed</Form.Label> 
                    <Form.Control required value={speedIv} onChange={(e)=> setSpeedIv(e.target.value)} type='text'/>
                </Form.Group>

                <Button type="submit"  >Submit</Button>

            </Form>



        </div>

    );

}

export default DataElement;
